scoringCase.score:
$trailing:=substituteAll(@"[^-a-z0-9.]", "", tmp_email_trailing, true);
$lenTrail:=length($trailing);
$leading:=substituteAll(@"[^-a-z0-9._]", "", tmp_email_leading, true);
$lenLead:=length($leading);
$sco:=$lenLead * 1000 + 10000 + iif($lenTrail > 6 and $lenTrail < 2, $lenTrail * 1000, 0);
min(500000, $sco) // score cannot be unbounded, scores by the number of supported characters

$trailing:=substituteAll(@"[^-a-z0-9.]", "", tmp_email_trailing, true);
$lenTrail:=length($trailing);
$leading:=substituteAll(@"[^-a-z0-9._!#$%&'*+/=?^`{|}~]", "", tmp_email_leading, true);
$lenLead:=length($leading);
$sco := $lenLead * 1000 + 13000 + iif($lenTrail > 6 and $lenTrail < 2, $lenTrail * 1000, 0);
min(1000000, $sco) // score cannot be unbounded, scores by the number of supported characters

scoringCase.explanation:
// union of minor explanations
$expl:=set.distinct(substituteAll(@"EMAIL_WS_SQUEEZED|EMAIL_ACCENTS_REMOVED|EMAIL_COMMAS_REPLACED|EMAIL_DOTS_REDUCED|EMAIL_NAME_MISSED_QUOTES|EMAIL_ENCAPSULED", "EMAIL_CLEANSED", exp_pur_email));
// remove general explanation if more specific is present
iif(containsWord(exp_pur_email, "EMAIL_TLD_MISSING_LAST_DOT_KNOWN_PROVIDER"), substituteAll(@"\bEMAIL_TLD_MISSING_LAST_DOT\b", "", $expl), $expl)
